import * as ts from 'typescript';
import { Logger } from './logger/Logger';
export const logger = new Logger();

import { DoxConfig } from './config/_namespace';
import {
	Branch,
	DoxProject,
	NpmPackage,
	Relation,
	TsDeclaration,
	TsReference,
	TsSourceFile,
} from './projectStructure/_namespace';
import { TscWrapper } from './tscApiWrapper/_namespace';

import * as serialise from './serialiser/_namespace';
import * as config from './config/_namespace';
import * as tsc from './tscApiWrapper/_namespace';
import main from '.';

export default main;
export {
	serialise,
	config,
	tsc,
	DoxConfig,
	Branch,
	DoxProject,
	NpmPackage,
	Relation,
	TsSourceFile,
	TsDeclaration,
	TsReference,
	TscWrapper,
};

/**
 * An enumerator for dox groups used to categorise `tsDeclarations`.
 */
export enum DeclarationGroup {
	unknown,
	ExportStar,
	ReExporter,
	Module,
	Variable,
	Function,
	Class,
	Enum,
	Type,
	Default,
}
export type tsConfigFile = string;
export type referenceName = string;

export * from './projectStructure/_namespace';

export type fileMap = Map<string, TsSourceFile>;
export type declarationMap = Map<string, TsDeclaration>;

export type referencedExport =
	| ts.NamespaceExport
	| ts.ExportSpecifier
	| ts.ModuleDeclaration;

export type localDeclaration = ts.VariableDeclaration | ts.ClassDeclaration;
export type declaration = referencedExport | localDeclaration;

export type logableObjects = TsDeclaration;

export type tsItem = ts.Node | ts.Symbol | ts.Type;

export interface rawDox {
	fileName: string;
	init: boolean;
	rootDir: string;
}
export interface tscRawConfig {
	dox: rawDox;
	config: {
		extends?: string;
		references?: ts.ProjectReference[];
		compilerOptions?: ts.CompilerOptions;
		exclude: string[];
		include: string[];
	};
	error?: ts.Diagnostic;
}

export type namedDef<def> = [string | undefined, def];
export type namedRegistry<reg> = Record<string, reg>;

export type npmPackageDefinition = [program: ts.Program, rootDir: string][];
export type npmPackageDefinitions = namedRegistry<npmPackageDefinition>;

export type projectOptions = config.doxGenericOptions<config.appConfApi>;

export namespace foo {
	const bar = 'foo';
}

import * as ts from 'typescript';
import * as utils from './loggerUtils';
import { Console } from 'node:console';

export class Logger extends Console {
	private logLevel: utils.logLevels;

	constructor(logLevel = utils.logLevels.info) {
		super(process.stdout, process.stderr, true);
		this.logLevel = logLevel;
	}
	public logLevels = utils.logLevels;
	public setLogLevel = (logLevel: utils.logLevels) => {
		this.logLevel = logLevel;
	};

	public inspect = utils.inspect;

	public debug = (...args: any) =>
		this.shouldLog(utils.logLevels.debug) &&
		super.debug(utils.colourise('Bright', '[debug]'), ...args);

	public info = (...args: any) =>
		this.shouldLog(utils.logLevels.info) && super.info('[info]', ...args);
	public infoKind = (kind: ts.SyntaxKind) => this.info(ts.SyntaxKind[kind]);
	public infoFlagSymbol = (flag: ts.SymbolFlags) =>
		this.info(ts.SymbolFlags[flag]);
	public infoFlagType = (flag: ts.TypeFlags) => this.info(ts.TypeFlags[flag]);

	public warn = (...args: any) =>
		this.shouldLog(utils.logLevels.warn) &&
		super.warn(utils.colourise('FgYellow', '[warning]'), ...args);

	public error = (...args: any) =>
		super.error(utils.colourise('FgRed', '[error]'), ...args);

	public throwError = (...args: any) => {
		this.error(...args);
		throw new Error();
	};

	public get stackTracer() {
		let i = 0;
		return Error()
			.stack?.replace('Error', '')
			.replace(/\n.*\n/g, (match) => {
				i++;
				return i === 1 ? '\n' : match;
			})!;
	}

	public identifier = utils.identifier;
	public logLevelKeyStrings = Object.keys(utils.logLevels).filter(
		(v) => !Number(v) && v !== '0',
	);
	public colourise = utils.colourise;
	public initLowerCamel = utils.initLowerCamel;
	public toLine = utils.toLine;

	public isClRequestForHelp = () =>
		utils.isRequestForHelp() ? utils.logApplicationHelp() : false;

	private shouldLog = (level: utils.logLevels) => level >= this.logLevel;
}

import * as path from 'path';
import util from 'util';
import { config, logger as log } from '../typedox';

export enum logLevels {
	debug = 0,
	info = 1,
	warn = 2,
	error = 3,
}

export function initLowerCamel(word: string) {
	return word[0].toLocaleLowerCase() + word.slice(1);
}

export const colourise = (color: keyof typeof c, text: string) =>
	c[color] + text + c.Reset;
const c = {
	Reset: '\x1b[0m',
	Bright: '\x1b[1m',
	Dim: '\x1b[2m',
	Underscore: '\x1b[4m',
	Blink: '\x1b[5m',
	Reverse: '\x1b[7m',
	Hidden: '\x1b[8m',

	FgBlack: '\x1b[30m',
	FgRed: '\x1b[31m',
	FgGreen: '\x1b[32m',
	FgYellow: '\x1b[33m',
	FgBlue: '\x1b[34m',
	FgMagenta: '\x1b[35m',
	FgCyan: '\x1b[36m',
	FgWhite: '\x1b[37m',
	FgGray: '\x1b[90m',

	BgBlack: '\x1b[40m',
	BgRed: '\x1b[41m',
	BgGreen: '\x1b[42m',
	BgYellow: '\x1b[43m',
	BgBlue: '\x1b[44m',
	BgMagenta: '\x1b[45m',
	BgCyan: '\x1b[46m',
	BgWhite: '\x1b[47m',
	BgGray: '\x1b[100m',
};

export function logApplicationHelp() {
	Object.keys(config.appConfApi).map((key) => {
		const helpItem = config.appConfApi[key];

		log.group(config.argHyphen + log.colourise('Underscore', key));
		log.log(helpItem.description);
		log.log('Default value:', helpItem.defaultValue);
		log.log();
		log.groupEnd();
	});
	return true;
}

export function identifier(subject: string | object) {
	const value =
		typeof subject === 'string'
			? `${path.basename(subject).split('.')[0]}`
			: subject.constructor && subject.constructor.name
			? `${initLowerCamel(subject.constructor.name)}`
			: getName(subject);

	return `[${value}]`;

	function getName(subject: any) {
		try {
			return subject.name;
		} catch (error) {
			return 'unknown';
		}
	}
}

export const isRequestForHelp = () =>
	process.argv.includes(`${config.argHyphen}help`);

export const logLevelKeyStrings = Object.keys(logLevels).filter(
	(v) => !Number(v) && v !== '0',
);

export function toLine(string: string) {
	return string.replace(/\s+/g, ' ');
}

export function inspect(object: any, ignoreKeys?: string[]): void;
export function inspect(object: any, shrink?: boolean): void;
export function inspect(
	object: any,
	shrink?: boolean,
	ignoreKeys?: string[],
): void;
export function inspect(
	object: any,
	ignoreOrShrink?: boolean | string[],
	ignore?: string[],
) {
	if (typeof object !== 'object') return log.log(...getArgs(object));

	const [hide, ignoreKeys] = resolveOverload(ignoreOrShrink, ignore);
	if (hide || ignoreKeys) object = shrink(object, hide, ignoreKeys);

	log.log(...getArgs(object));

	function getArgs(obj = object) {
		return ['[inspect]', util.inspect(obj, false, null, true)];
	}
	function resolveOverload(
		ignoreOrShrink: boolean | string[] | undefined,
		ignore: string[] | undefined,
	): [boolean | undefined, string[] | undefined] {
		return typeof ignoreOrShrink === 'boolean'
			? [ignoreOrShrink as boolean, ignore]
			: !!ignoreOrShrink
			? [undefined, ignoreOrShrink as string[]]
			: [undefined, undefined];
	}
}

function shrink(
	item: any,
	hide = false,
	unwantedKeys: string[] = [],
	seen: Map<object, true> = new Map(),
): any {
	const isArray = Array.isArray(item);
	const isObject = typeof item == 'object';
	if (!isArray && !isObject) return item;

	const objectCopy = isArray
		? [...item]
				.filter(
					(value) =>
						(!!value || !hide) && !unwantedKeys.includes(value),
				)
				.map((value) =>
					typeof value === 'object'
						? (unwantedKeys.includes(value) && `[hidden]`) ||
						  shrink(value, hide, unwantedKeys, seen)
						: value,
				)
		: (Object.entries({ ...item }).reduce(
				(accumulator, tuple) => {
					const [key, value] = tuple;
					const hasValue = typeof value === 'boolean' || !!value;
					if (
						(hide && !hasValue) ||
						(hide && unwantedKeys.includes(key)) ||
						seen.has(value as object)
					) {
						return accumulator;
					}
					seen.set(value as object, true);

					const len = Array.isArray(value)
						? `(${value.length}): Array`
						: typeof value === 'object' &&
						  `(${Object.keys(value as object).length}): Object`;

					accumulator[key] = unwantedKeys.includes(key)
						? `[hidden ${len || typeof value}]`
						: shrink(value, hide, unwantedKeys, seen);

					return accumulator;
				},
				{} as Record<string, any>,
		  ) as object);
	return objectCopy;
}

export * from './libArgs';
export * from './libOpts';
export * from './libConfig';
export * from './doxConfigApi';
export { DoxConfig } from './DoxConfig';

import * as path from 'path';
import { logger as log, config } from '../typedox';

let _splitArgs: { doxClArgs: string[]; tscClArgs: string[] };
export function getDoxClArgs<Args extends config.doxArgs>(
	doxArgs: config.doxGenericArgs<Args>,
) {
	return _extrudeDoxClArgValues(doxArgs).doxClArgs;
}
export function getTscClArgs() {
	if (!_splitArgs)
		log.throwError(
			log.identifier(__filename),
			'Cannot call for tsc Commandline args before extracting doxClArgs',
		);
	return _splitArgs.tscClArgs;
}
export function getDoxConfigFilepathFromClArgs(coreArgs: config.appConfApi) {
	let typedox = '';
	let rootDir = '';
	process.argv.forEach((value, index) => {
		value === '--typedox' &&
			process.argv[index + 1] &&
			(typedox = process.argv[index + 1]);
		value === '--projectRootDir' &&
			process.argv[index + 1] &&
			(rootDir = process.argv[index + 1]);
	});

	typedox.startsWith(argHyphen) && (typedox = '');
	rootDir.startsWith(argHyphen) && (rootDir = '');
	if (!typedox && !rootDir)
		return path.join(
			coreArgs.projectRootDir.defaultValue,
			coreArgs.typedox.defaultValue,
		);

	const absRoot = rootDir && path.isAbsolute(rootDir);
	const absDox = typedox && path.isAbsolute(typedox);
	if (absRoot && absDox && !typedox.startsWith(rootDir))
		log.throwError(
			log.identifier(__filename),
			'typedox.json must exist under the project root directory',
		);
	if (absDox) return path.resolve(typedox);
	!rootDir && (rootDir = coreArgs.projectRootDir.defaultValue);
	typedox = path.join(rootDir, typedox);
	return path.isAbsolute(typedox)
		? typedox
		: path.join(coreArgs.projectRootDir.defaultValue, typedox);
}

export const convertArgObjectToCommandLineKeys = <
	argsInterface extends config.doxArgs,
>(
	rawArgDefs: config.doxGenericArgs<argsInterface>,
) => Object.keys(rawArgDefs).map((projectArg) => hyphenateArg(projectArg));

export const argHyphen = '--';
export const hyphenateArg = (arg: string) => `${argHyphen}${arg}`;
export const unHyphenateArg = (arg: `${typeof argHyphen}${string}`) =>
	arg.replace(argHyphen, '');

function _extrudeDoxClArgValues<Args extends config.doxArgs>(
	doxArgs: config.doxGenericArgs<Args>,
) {
	if (_splitArgs) return _splitArgs;
	const doxClArgs: string[] = [];
	const doxClKeys = convertArgObjectToCommandLineKeys<Args>(doxArgs);
	let isDox = false;

	const tscClArgs = process.argv.filter((arg) => {
		isDox = doxClKeys.includes(arg)
			? true
			: arg.startsWith('-')
			? false
			: isDox;

		isDox && doxClArgs.push(arg);
		return !isDox;
	});
	_splitArgs = { doxClArgs, tscClArgs };
	return _splitArgs;
}

import * as ts from 'typescript';
import { logger as log, config, projectOptions } from '../typedox';

type Args = config.appConfApi;

export function getDoxOptions(customOptions?: projectOptions) {
	const doxArgs = config.appConfApi;
	const options = customOptions || mergeOptions();

	validateDoxOptions(doxArgs, options);
	return options;

	function mergeOptions() {
		const defaultOptions = getDefaultDoxOptions<Args>(doxArgs);
		let { fileArgs } = config.readDoxConfigFromFile(doxArgs);

		const clOptions = config.getDoxConfigFromCommandLine<Args>(doxArgs);

		const options = {
			...defaultOptions,
			...fileArgs,
			...clOptions,
		} as projectOptions;

		return options;
	}
}

export function getTscParsedCommandline() {
	const tscOptions = ts.parseCommandLine(config.getTscClArgs());
	tscOptions.errors.forEach((error) => {
		log.warn(
			log.identifier(__filename),
			log.identifier(ts.parseCommandLine),
			error.messageText,
		);
	});
	return tscOptions;
}

export function getDefaultDoxOptions<Args extends config.doxArgs>(
	doxArgs = config.appConfApi,
) {
	const argTuples = Object.entries(doxArgs).map((tuple) => {
		const [key, item] = tuple;
		return [key, item.defaultValue];
	});
	const optionObject = Object.fromEntries(argTuples);

	return optionObject as config.doxGenericOptions<Args>;
}
function validateDoxOptions(
	coreArgs: config.appConfApi,
	options: config.doxGenericOptions<Args>,
) {
	Object.keys(coreArgs).forEach((key) => {
		const coreArg = coreArgs[key];
		const optionValue = options[key];
		const notFound = !optionValue;
		const validated = coreArg.validate(optionValue);

		if (coreArg.required && (notFound || validated === undefined))
			log.throwError(
				log.identifier(__filename),
				'A required option was not found:',
				key,
			);
		if (!validated)
			log.throwError(
				log.identifier(__filename),
				'An invalid option was found:',
				key,
			);
	});
}

import * as fs from 'fs';

import { logger as log, config, DoxConfig } from '../typedox';

export function getDoxConfigFromCommandLine<Args extends config.doxArgs>(
	doxArgs: config.doxGenericArgs<Args>,
) {
	const doxClArgs = config.getDoxClArgs<Args>(doxArgs);
	const doxConfig = getDoxClOptions<Args>(
		doxArgs,
		doxClArgs,
		{} as config.doxGenericOptions<Args>,
	);
	return doxConfig;
}

export function readDoxConfigFromFile(coreArgs: config.appConfApi) {
	type doxOptions = config.doxGenericOptions<config.appConfApi>;
	const optionsFile =
		config.getDoxConfigFilepathFromClArgs(coreArgs) ||
		coreArgs.typedox.defaultValue;

	const validOptions = Object.keys(coreArgs);
	const fileArgs: doxOptions = fs.existsSync(optionsFile)
		? DoxConfig.jsonFileToObject(optionsFile)
		: {};

	Object.keys(fileArgs).forEach((key) => {
		const coreArg = coreArgs[key];
		const fileArg = fileArgs[key];
		const validKeyError = !validOptions.includes(key);
		const isRequiredError = coreArg.required && !fileArg;
		if (!coreArg.required && fileArg === '') return delete fileArgs[key];
		if (validKeyError || isRequiredError) {
			warnAboutInvalidOption(key, coreArgs, optionsFile, validKeyError);
			delete fileArgs[key];
		}
	});

	return { fileArgs, optionsFile };
}

export const configValidators = {
	projectRootDir: (value: any) =>
		validator(value, function (value: any) {
			return !!value && typeof value === 'string';
		}),
	doxOut: (value: any) =>
		validator(value, function (value: any) {
			return !!value && typeof value === 'string';
		}),
	typeDependencies: (value: any) =>
		validator(value, function (value: any) {
			return (
				Array.isArray(value) &&
				!value.find((value) => typeof value !== 'string')
			);
		}),
	logLevel: (value: any) =>
		validator(value, function (value: any) {
			return log.logLevelKeyStrings.includes(value);
		}),
	tsConfigs: (value: any) =>
		validator(value, (value: any) => {
			return !Array.isArray(value)
				? false
				: !value.find((innerValue) => {
						return checkInnerTsconfigValue(innerValue);
				  });
		}),
	npmFileConvention: (value: any) =>
		validator(
			value,
			(value: any) =>
				typeof value === 'string' && value.split('.').length > 1,
		),
	typedox: (value: any) =>
		validator(
			value,
			(value) => typeof value === 'string' && value.split('.').length > 1,
		),
};

export const configSetters = {
	projectRootDir: function (doxOptions: config.coreOpts, value: string) {
		doxOptions.projectRootDir = value;
	},
	doxOut: function (doxOptions: config.coreOpts, value: string) {
		doxOptions.doxOut = value;
	},
	typeDependencies: function (
		doxOptions: config.coreOpts,
		value: string,
	): void {
		!doxOptions.typeDependencies &&
			(doxOptions.typeDependencies =
				config.appConfApi.typeDependencies.defaultValue);

		!doxOptions.typeDependencies.includes(value) &&
			doxOptions.typeDependencies.push(value);
	},
	logLevel: function (doxOptions: config.coreOpts, value: string) {
		doxOptions.logLevel = value as keyof typeof log.logLevels;
	},
	tsConfigs: function (doxOptions: config.coreOpts, value: string) {
		!doxOptions.tsConfigs &&
			(doxOptions.tsConfigs = config.appConfApi.tsConfigs.defaultValue);

		!doxOptions.tsConfigs.includes(value) &&
			doxOptions.tsConfigs.push(value);
	},
	npmFileConvention: function (doxOptions: config.coreOpts, value: string) {
		doxOptions.npmFileConvention = value;
	},
	typedox: function (doxOptions: config.coreOpts, value: string) {
		doxOptions.typedox = value;
	},
};

function validator(value: any, callback: (value: any) => boolean) {
	if (!value) return undefined;
	return callback(value);
}
function checkInnerTsconfigValue(innerValue: any) {
	return typeof innerValue === 'string'
		? false
		: Array.isArray(innerValue)
		? !innerValue.find((innerString) => typeof innerString !== 'string')
		: true;
}

function warnAboutInvalidOption(
	key: string,
	coreArgs: config.appConfApi,
	optionsFile: string,
	isType: boolean = false,
) {
	log.warn(
		log.identifier(__filename),
		`Invalid option ${isType ? 'type ' : ''}found in ${optionsFile}:`,
		`"${key}".`,
		'The value was replaced with the default value.',
	);
	isType && log.log('Allowed Options:', Object.keys(coreArgs));
	return true;
}

function getDoxClOptions<Args extends config.doxArgs>(
	doxArgs: config.doxGenericArgs<Args>,
	doxClArgs: string[],
	doxOptions: config.doxGenericOptions<Args>,
) {
	type thisArgs = config.doxGenericArgs<Args>;
	type argKey = keyof thisArgs;
	type clArgKey = `--${string}`;

	const clKeys = config.convertArgObjectToCommandLineKeys<Args>(doxArgs);

	let clKey: clArgKey;
	doxClArgs.forEach((clArg, index) => {
		clKey = clKeys.includes(clArg) ? (clArg as clArgKey) : clKey;

		const doxArg = config.unHyphenateArg(clKey) as argKey;
		const set = doxArgs[doxArg].set;
		const defaultValue = doxArgs[doxArg].defaultValue;
		const valueIsDoxKey = clKeys.includes(clArg);
		const parent = doxClArgs[index + 1];
		const isOrphan = !parent || parent.startsWith(config.argHyphen);

		valueIsDoxKey && isOrphan
			? adoptOrphan()
			: !valueIsDoxKey && set(doxOptions, clArg);

		function adoptOrphan() {
			typeof defaultValue === 'boolean'
				? set(doxOptions, true)
				: (doxOptions[doxArg] = defaultValue);
		}
	});

	return doxOptions;
}

import { logger as log } from '../typedox';
import { configSetters, configValidators } from './libConfig';

export type doxArgs = Record<string, doxConfigArg<any, any, any>>;
export interface doxConfigArg<
	defaultValue,
	value,
	argsInterface extends doxArgs,
> {
	description: string;
	defaultValue: defaultValue;
	required: defaultValue | undefined;
	set: (doxOptions: doxGenericOptions<argsInterface>, value: value) => void;
	validate: (value: any) => undefined | boolean;
}
export type doxGenericOptions<Args extends doxArgs> = {
	[K in keyof Args]: Args[K]['defaultValue'] | Args[K]['required'];
};
export type doxGenericArgs<argsInterface extends doxArgs> = {
	[K in keyof argsInterface]: doxConfigArg<any, any, argsInterface>;
};
export type coreOpts = doxGenericOptions<appConfApi>;

export interface appConfApi extends doxArgs {
	projectRootDir: doxConfigArg<string, string, appConfApi>;
	doxOut: doxConfigArg<string, string, appConfApi>;
	typeDependencies: doxConfigArg<string[], string, appConfApi>;
	logLevel: doxConfigArg<keyof typeof log.logLevels, string, appConfApi>;
	tsConfigs: doxConfigArg<string[], string, appConfApi>;
	npmFileConvention: doxConfigArg<string, string, appConfApi>;
	typedox: doxConfigArg<string, string, appConfApi>;
}

export const appConfApi: appConfApi = {
	projectRootDir: {
		description: 'The absolute path location of the project root',
		defaultValue: process.cwd(),
		required: process.cwd(),
		set: configSetters.projectRootDir,
		validate: configValidators.projectRootDir,
	},
	doxOut: {
		description: 'The out directory for document build files',
		defaultValue: 'docs',
		required: 'docs',
		set: configSetters.doxOut,
		validate: configValidators.doxOut,
	},
	typeDependencies: {
		description:
			'The @types dependencies in `node_modules` to be included for documentation, if any.',
		defaultValue: [] as string[],
		required: [] as string[],
		set: configSetters.typeDependencies,
		validate: configValidators.typeDependencies,
	},
	logLevel: {
		description: `One of [${log.logLevelKeyStrings}]`,
		defaultValue: 'info',
		required: 'info',
		set: configSetters.logLevel,
		validate: configValidators.logLevel,
	},
	tsConfigs: {
		description: `Specific tsconfig files to used as documentation entry points.`,
		defaultValue: [],
		required: undefined,
		set: configSetters.tsConfigs,
		validate: configValidators.tsConfigs,
	},
	npmFileConvention: {
		description: 'The name convention of the json files used to set up npm',
		defaultValue: 'package.json',
		required: 'package.json',
		set: configSetters.npmFileConvention,
		validate: configValidators.npmFileConvention,
	},
	typedox: {
		description: 'File location of typedox.json config file',
		defaultValue: 'typedox.json',
		required: undefined,
		set: configSetters.typedox,
		validate: configValidators.typedox,
	},
};

import * as path from 'path';
import * as ts from 'typescript';
import * as fs from 'fs';

import {
	logger as log,
	tscRawConfig,
	config,
	TscWrapper,
	tsItem,
	serialise,
	DoxProject,
	NpmPackage,
	TsReference,
	tsc,
	projectOptions,
} from '../typedox';
import { appConfApi } from './doxConfigApi';

/** get a handle for future jsconfig etc fun */
export const tsFileSpecifier = 'tsconfig';

let _tscCommandlineConfig: ts.ParsedCommandLine;
let _clProject: string[] | undefined;
let _customProject: string[] | undefined;
let _entryProject: string[];
let _tscRawConfigs: tscRawConfig[];
let _tscParsedConfigs: ts.ParsedCommandLine[];
let _projectOptions: projectOptions;

export class DoxConfig {
	public checker?: ts.TypeChecker;

	constructor(checker?: ts.TypeChecker);
	constructor(projectOptions: projectOptions, checker?: ts.TypeChecker);
	constructor(
		projectOrChecker?: projectOptions | ts.TypeChecker,
		optionalChecker?: ts.TypeChecker,
	) {
		const [projectOptions, checker] = resolveConstructorOverload(
			projectOrChecker,
			optionalChecker,
		);
		this.checker = checker;

		_projectOptions ??= projectOptions! as projectOptions;
		_tscCommandlineConfig = _tscCommandlineConfig ??=
			config.getTscParsedCommandline();

		_clProject ??= this._clProject();
		_customProject ??= this._customProject();
		_entryProject ??= this._entryProject();
		_tscRawConfigs ??= this._tscRawConfigs();
		_tscParsedConfigs ??= this._tscParsedConfigs();

		if (!_projectOptions)
			log.throwError(
				log.identifier(__filename),
				'must be initiated with project options.',
			);
	}

	public get options() {
		return {
			projectRootDir: this.projectRootDir,
			doxout: this.doxOut,
			dependTypes: this.dependTypes,
			logLevel: this.logLevel,
			npmFileConvention: this.npmFileConvention,
			tsConfigs: this.tsConfigs,
		};
	}

	public get toObject() {
		const constructor = this.constructor.name;
		const self = this as unknown;
		return constructor === 'DoxProject'
			? serialise.serialiseProject(self as DoxProject)
			: constructor === 'NpmPackage'
			? serialise.serialiseNpmPackage(self as NpmPackage)
			: constructor === 'TsReference'
			? serialise.serialiseTsReference(self as TsReference)
			: log.error(
					log.identifier(__filename),
					'Call made to unknown serialiser:',
					constructor,
			  );
	}

	protected get tscParsedConfigs() {
		return _tscParsedConfigs;
	}

	private get projectRootDir() {
		return path.resolve(_projectOptions.projectRootDir!);
	}
	private get doxOut() {
		return DoxConfig.ensureAbsPath(
			this.projectRootDir,
			_projectOptions.doxOut!,
		);
	}
	private get dependTypes() {
		return _projectOptions.typeDependencies || [];
	}
	private get logLevel() {
		return log.logLevels[_projectOptions.logLevel!];
	}
	private get npmFileConvention() {
		return _projectOptions.npmFileConvention!;
	}
	private get tsConfigs() {
		return _clProject
			? _clProject
			: _customProject?.length
			? _customProject
			: _entryProject;
	}

	public isSpecifierKind = (kind: ts.SyntaxKind) => {
		const {
			NamespaceExport,
			NamespaceImport,
			ModuleDeclaration,
			ExportDeclaration,
			ExportSpecifier,
			ExportAssignment,
			ImportClause,
			ImportSpecifier,
		} = ts.SyntaxKind;
		const specifiers = [
			NamespaceExport,
			NamespaceImport,
			ModuleDeclaration,
			ExportDeclaration,
			ExportSpecifier,
			ExportAssignment,
			ImportClause,
			ImportSpecifier,
		];

		return specifiers.includes(kind);
	};

	protected tsWrap = (item: tsItem): TscWrapper => {
		!this.checker && notices.tsWrap.throw(log.stackTracer);
		return tsc.wrap(this.checker!, item);
	};

	private get tscCommandLineOptions() {
		const clOptions = {
			..._tscCommandlineConfig.options,
		} as ts.CompilerOptions;
		clOptions.types = this.dependTypes;
		return clOptions;
	}
	private _clProject = () => {
		let project = this.tscCommandLineOptions.project;
		const def = project
			? [DoxConfig.ensureAbsPath(this.projectRootDir, project)]
			: undefined;

		return def;
	};
	private _customProject = () => {
		return _projectOptions.tsConfigs?.map((fileName) =>
			DoxConfig.ensureAbsPath(this.projectRootDir, fileName),
		);
	};
	private _entryProject = () => {
		return [ts.findConfigFile(this.projectRootDir, ts.sys.fileExists)!];
	};
	private _tscRawConfigs = (): tscRawConfig[] => {
		const isRootInit = !!_entryProject || !!_clProject;
		const rawConfigs = findAllRawConfigs(
			this.tsConfigs,
			DoxConfig.ensureAbsPath.bind(null, this.projectRootDir),
			isRootInit,
		);
		return rawConfigs;
	};
	private _tscParsedConfigs = () => {
		const isRootLevel = _entryProject || _clProject;
		const existingOptions = isRootLevel ? this.tscCommandLineOptions : {};

		const parsedConfigs = makeParsedConfigs(
			_tscRawConfigs,
			this.dependTypes,
			existingOptions,
		);

		return parsedConfigs;
	};

	public static jsonFileToObject(absFilepath: string) {
		DoxConfig.ensureFileExists(absFilepath);
		const sourceFile = ts.readJsonConfigFile(absFilepath, ts.sys.readFile);
		const diagnostics: ts.Diagnostic[] = [];
		const object = ts.convertToObject(sourceFile, diagnostics);
		diagnostics.forEach((diagnostic) =>
			log.warn(log.identifier(__filename), diagnostic.messageText),
		);

		return object;
	}
	public static ensureFileExists(filepath: string) {
		if (!fs.existsSync(filepath)) {
			log.throwError(
				log.identifier(__filename),
				'File not found:',
				filepath,
			);
		}
	}
	public static ensureAbsPath(rootDir: string, location: string) {
		if (path.isAbsolute(location)) return location;
		return path.join(rootDir, location);
	}
}
function resolveConstructorOverload(
	optionsOrChecker: any,
	checker: any,
): [projectOptions | undefined, ts.TypeChecker | undefined] {
	if (!optionsOrChecker && !checker) return [undefined, undefined];
	if (optionsOrChecker && checker)
		return [optionsOrChecker as projectOptions, checker as ts.TypeChecker];

	const isOptions = isEqual(optionsOrChecker, config.appConfApi);
	return isOptions
		? [optionsOrChecker as projectOptions, undefined]
		: [undefined, optionsOrChecker as ts.TypeChecker];

	function isEqual(value: any, value2: any) {
		const keys = Object.keys(value);
		const keys2 = Object.keys(value2);

		return keys.every((key) => keys2.includes(key));
	}
}
function makeParsedConfig(
	dependTypes: string[],
	existingOptions: ts.CompilerOptions,
	tscRawConfig: tscRawConfig,
) {
	const { rootDir, fileName } = tscRawConfig.dox;
	const { compilerOptions } = tscRawConfig.config;
	compilerOptions && (compilerOptions.types = dependTypes);

	const parsedConfig = ts.parseJsonConfigFileContent(
		tscRawConfig.config,
		ts.sys,
		rootDir,
		existingOptions,
		fileName,
	) as ts.ParsedCommandLine;

	return parsedConfig;
}
function makeParsedConfigs(
	tscRawConfigs: tscRawConfig[],
	dependTypes: string[],
	existingOptions: ts.CompilerOptions,
) {
	const parseConfig = makeParsedConfig.bind(
		null,
		dependTypes,
		existingOptions,
	);
	const parsedConfigs = tscRawConfigs.map(parseConfig);

	return parsedConfigs;
}

function findAllRawConfigs(
	configFilePaths: string[],
	ensureAbsPath: (path: string) => string,
	isRootInit: boolean = false,
	accumulator: tscRawConfig[] = [],
): tscRawConfig[] {
	const tscRawConfigs = configFilePaths.reduce(
		mergeConfigReferences.bind(isRootInit),
		accumulator,
	);
	return tscRawConfigs;

	function mergeConfigReferences(
		this: boolean,
		accumulator: tscRawConfig[],
		fileName: string,
	) {
		const isInit = this;
		const rawConfig = makeRawTscConfigFromFile(fileName, isInit);

		accumulator.push(rawConfig);

		const references = discoverReferences(rawConfig);

		return references.length
			? findAllRawConfigs(references, ensureAbsPath, false, accumulator)
			: accumulator;
	}

	function discoverReferences(rawConfig: tscRawConfig) {
		const references = (rawConfig.config.references || [])
			.map(resolveReference)
			.filter((reference) => !!reference);

		return references as string[];
	}
	function resolveReference(reference: ts.ProjectReference) {
		const referencePath = ts.resolveProjectReferencePath(reference);
		!referencePath &&
			log.warn(
				log.identifier(__filename),
				'Did not resolve a reference:',
				reference.path,
			);
		return referencePath ? ensureAbsPath(referencePath) : undefined;
	}
}
function makeRawTscConfigFromFile(fileName: string, init: boolean) {
	const rootDir = path.dirname(fileName);
	const rawConfig = readTscConfigFile(fileName) as tscRawConfig;

	rawConfig.dox = {
		fileName,
		init,
		rootDir,
	};

	return rawConfig;

	function readTscConfigFile(configPath: string) {
		return ts.readConfigFile(configPath, ts.sys.readFile) as tscRawConfig;
	}
}

const notices = {
	tsWrap: {
		throw: function (trace: string) {
			log.throwError(
				log.identifier(__filename),
				'Typechecker has not been registered yet',
				trace,
			);
		},
	},
};

