/**
 * A container for a typescript compiler reference. This could be the only one in a npm package, or one of many if
 * the typescript `references` option is used. Each reference will have a corresponding `tsconfig`.
 *
 * &emsp;DoxProject\
 * &emsp;&emsp;|\
 * &emsp;&emsp;--- NpmPackage[]\
 * &emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;--- **TsReference**[]\
 * &emsp;&emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;&emsp;--- TsSourceFile[]\
 * &emsp;&emsp;&emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;&emsp;&emsp;--- TsDeclaration[]\
 * &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;--- Branch[]\
 * &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;...TsDeclaration...
 *
 */
export class TsReference extends DoxConfig {
	public name: string;
	public parent: NpmPackage;
	public filesMap = new Map<string, TsSourceFile>();
	public treeBranches: Map<string, Branch> = new Map();
	public entryFileList: string[];
	private program: ts.Program;

	constructor(
		parent: NpmPackage,
		name: string,
		program: ts.Program,
		files: string[],
	) {
		super(program.getTypeChecker());

		this.name = name;
		this.parent = parent;
		this.entryFileList = files;
		this.program = program;
		/*
		files.forEach((fileName) => {
			const checker = program.getTypeChecker();
			const foo = program.getSourceFile(fileName)!;
			this.filesMap.set(fileName, new TsSourceFile(this, foo, checker));
		});
*/
		//this.entryFileList = context.tsConfig.fileNames;
	}
	/*
	public get toObject() {
		return dox.serialise.serialiseTsReference(this);
	}
*/
	public discoverDeclarations = () => {
		this.filesMap.forEach((file) => file.discoverDeclarations());
	};

	public buildRelationships = () =>
		this.filesMap.forEach((file) => file.buildRelationships());

	public discoverFiles = this.discoverFilesRecursively;
	private discoverFilesRecursively(fileList = this.entryFileList) {
		fileList.forEach((fileName) => {
			if (this.filesMap.has(fileName)) return;
			const fileSource = this.program.getSourceFile(fileName);

			if (!fileSource)
				return log.error(
					log.identifier(__filename),
					'No source file was found:',
					fileName,
				);

			const fileSymbol = this.checker?.getSymbolAtLocation(fileSource);

			if (!fileSymbol)
				return log.info(
					log.identifier(__filename),
					'File was not included as part of the documentation set:',
					fileName,
				);

			const doxSourceFile = new TsSourceFile(
				this,
				fileSource,
				fileSymbol,
			);
			this.filesMap.set(fileName, doxSourceFile);

			this.discoverFilesRecursively(doxSourceFile.childFiles);
		});
	}
	public static getDeclarationRoots = (sourceFiles: TsSourceFile[]) => {
		return this.getAllDeclarations(sourceFiles).filter(
			(declaration) => !declaration.parents.length,
		);
	};

	private static getAllDeclarations = (sourceFiles: TsSourceFile[]) => {
		return sourceFiles
			.map((fileSource) => [...fileSource.declarationsMap.values()])
			.flat();
	};
}

import * as ts from 'typescript';
import {
	DoxConfig,
	Relation,
	TsDeclaration,
	TsReference,
	declarationMap,
	logger as log,
	tsc,
} from '../typedox';

/**
 * A container for typescript compiler source files:
 *
 * &emsp;DoxProject\
 * &emsp;&emsp;|\
 * &emsp;&emsp;--- NpmPackage[]\
 * &emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;--- TsReference[]\
 * &emsp;&emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;&emsp;--- **TsSourceFile**[]\
 * &emsp;&emsp;&emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;&emsp;&emsp;--- TsDeclaration[]\
 * &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;--- Branch[]\
 * &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;...TsDeclaration...
 */
export class TsSourceFile extends DoxConfig {
	public parent: TsReference;
	public childFiles: string[];
	public fileName: string;
	public fileSymbol: ts.Symbol;
	public fileType: ts.Type;
	public declarationsMap: declarationMap = new Map();
	public source: ts.SourceFile;

	constructor(
		parent: TsReference,
		source: ts.SourceFile,
		fileSymbol: ts.Symbol,
	) {
		super(parent.checker);
		this.parent = parent;
		this.checker = this.checker!;

		this.source = source;
		this.fileName = source.fileName;
		this.fileSymbol = fileSymbol;
		this.fileType = this.checker.getTypeOfSymbol(this.fileSymbol);
		const fileExports = this.fileSymbol.exports?.values();
		this.childFiles = this.discoverFiles([...(fileExports || [])]);

		log.debug(log.identifier(this), this.fileName);
	}

	private discoverFiles = (fileSymbols: ts.Symbol[]) => {
		return fileSymbols.map(getFileNames.bind(this)).flat().filter(filter);

		function getFileNames(this: TsSourceFile, symbol: ts.Symbol) {
			return tsc.isStarExport(symbol)
				? discoverStars.bind(this)(symbol)
				: this.tsWrap(symbol).targetFileName!;
		}
		function discoverStars(this: TsSourceFile, symbol: ts.Symbol) {
			return tsc.parseExportStars
				.call(this, symbol)
				.map((expression) => this.tsWrap(expression).targetFileName!);
		}
		function filter(value: string, index: number, array: string[]) {
			return !!value && array.indexOf(value) === index;
		}
	};
	public discoverDeclarations = () => {
		this.fileSymbol.exports?.forEach(parseSymbol.bind(this));

		function parseSymbol(this: TsSourceFile, symbol: ts.Symbol) {
			tsc.isStarExport(symbol)
				? discoverStarExports.bind(this)(symbol)
				: makeDeclaration.call(this, symbol);
		}
		function discoverStarExports(this: TsSourceFile, symbol: ts.Symbol) {
			tsc.parseExportStars
				.call(this, symbol)
				.forEach(makeDeclaration.bind(this));
		}
		function makeDeclaration(
			this: TsSourceFile,
			item: ts.Symbol | ts.Node,
		) {
			const declaration = new TsDeclaration(this, item);
			this.declarationsMap.set(declaration.name, declaration);
		}
	};
	public buildRelationships = () => {
		this.declarationsMap.forEach(
			(declaration) => new Relation(this, declaration),
		);
	};
}

import * as ts from 'typescript';
import {
	DeclarationGroup,
	DoxConfig,
	DoxProject,
	TsSourceFile,
	TscWrapper,
	declarationMap,
	logger as log,
} from '../typedox';

/**
 * A container for typescript declarations:
 *
 * &emsp;DoxProject\
 * &emsp;&emsp;|\
 * &emsp;&emsp;--- NpmPackage[]\
 * &emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;--- TsReference[]\
 * &emsp;&emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;&emsp;--- TsSourceFile[]\
 * &emsp;&emsp;&emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;&emsp;&emsp;--- **TsDeclaration**[]\
 * &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;--- Branch[]\
 * &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;...**TsDeclaration**...
 *
 *
 */
export class TsDeclaration extends DoxConfig {
	parent: TsSourceFile;
	name: string;
	tsKind: ts.SyntaxKind;
	tsNode: ts.Node;
	tsSymbol: ts.Symbol;
	tsType: ts.Type;
	nameSpace?: string;
	parents: TsDeclaration[] = [];
	children: declarationMap = new Map();
	aliasName?: string;
	get: TscWrapper;

	constructor(parent: TsSourceFile, item: ts.Symbol | ts.Node) {
		super(parent.checker);
		this.parent = parent;

		this.get = this.tsWrap(item);

		this.name = this.get.name;
		this.tsKind = this.get.kind;
		this.tsNode = this.get.tsNode;
		this.tsSymbol = this.get.tsSymbol;
		this.tsType = this.get.tsType;

		if (!this.get.isExportStarChild && !this.isSpecifierKind(this.tsKind))
			return;

		this.parser(this.get.tsNode);
	}
	public get kind() {
		const { SyntaxKind } = ts;

		if (this.get.isExportStarChild) return DeclarationGroup.ExportStar;

		const tsKind = TsDeclaration.resolveTsKind(this);

		const isModule =
			tsKind === SyntaxKind.ModuleDeclaration ||
			tsKind === SyntaxKind.NamespaceExport ||
			tsKind === SyntaxKind.NamespaceImport;
		const isType =
			tsKind === SyntaxKind.TypeAliasDeclaration ||
			tsKind === SyntaxKind.InterfaceDeclaration;

		const kind = isModule
			? DeclarationGroup.Module
			: isType
			? DeclarationGroup.Type
			: tsKind === SyntaxKind.ImportSpecifier
			? DeclarationGroup.ReExporter
			: tsKind === SyntaxKind.VariableDeclaration
			? DeclarationGroup.Variable
			: tsKind === SyntaxKind.ClassDeclaration
			? DeclarationGroup.Class
			: tsKind === SyntaxKind.FunctionDeclaration
			? DeclarationGroup.Function
			: tsKind === SyntaxKind.EnumDeclaration
			? DeclarationGroup.Enum
			: tsKind === SyntaxKind.ExportAssignment
			? DeclarationGroup.Default
			: DeclarationGroup.unknown;

		kind === DeclarationGroup.unknown && notices.kind(tsKind, this.get);

		return kind;
	}

	private parser(node: ts.Node, get = this.get, isTarget = false) {
		if (!this.isSpecifierKind(node.kind)) return;

		ts.isModuleDeclaration(node)
			? this.parseModuleDeclaration(node)
			: ts.isNamespaceExport(node)
			? this.parseNamespaceExport()
			: ts.isExportSpecifier(node)
			? this.parseExportSpecifier()
			: ts.isImportSpecifier(node)
			? this.parseImportSpecifier()
			: get.isExportStarChild
			? this.parseReExporter(get)
			: ts.isExportAssignment(node)
			? this.parseExportAssignment(get)
			: ts.isNamespaceImport(node)
			? this.parseNamespaceImport(get)
			: notices.parser.deepreport.call(this, isTarget, get);
	}

	private parseExportAssignment(get: TscWrapper) {
		notices.parse.debug.call(this, 'parseExportAssignment');
		//log.inspect(get.tsNode, true, ['parent']);
	}
	private parseReExporter(get: TscWrapper) {
		notices.parse.debug.call(this, 'parseReExporter');
		//this.info(get.tsSymbol.exports);
	}
	private parseModuleDeclaration(module: ts.ModuleDeclaration) {
		notices.parse.debug.call(this, 'parseModuleDeclaration');

		this.nameSpace = module.name.getText();
	}
	private parseNamespaceExport = () => {
		notices.parse.debug.call(this, 'parseNamespaceExport');

		this.nameSpace = this.name;
	};
	private parseNamespaceImport = (get: TscWrapper) => {
		notices.parse.debug.call(this, 'parseNamespaceImport');

		this.nameSpace = this.name;
	};
	private parseImportSpecifier() {
		notices.parse.debug.call(this, 'parseImportSpecifier');

		const target = this.get.immediatelyAliasedSymbol;
		const get = this.tsWrap(target!);

		//this.parser(get.tsNode, get, true);
	}
	private parseExportSpecifier() {
		notices.parse.debug.call(this, 'parseExportSpecifier');

		const localTarget = this.get.localTargetDeclaration;

		if (!localTarget)
			return log.error(
				log.identifier(this),
				'No local target found:',
				this.get.report,
			);
		const get = this.tsWrap(localTarget);
		this.parser(get.tsNode, get, true);
	}

	private static resolveTsKind(declaration: TsDeclaration) {
		let tsKind = declaration.tsKind;
		let { get } = declaration;

		if (get.localTargetDeclaration) {
			get = declaration.tsWrap(get.localTargetDeclaration);
			tsKind = get.tsNode.kind;
		}
		if (ts.isImportSpecifier(get.tsNode)) {
		}
		if (ts.isExportSpecifier(get.tsNode)) {
			log.info('---------------------------------------------');
		}
		if (
			tsKind === ts.SyntaxKind.VariableDeclaration &&
			get.callSignatures.length
		) {
			tsKind = ts.SyntaxKind.FunctionDeclaration;
		}

		return tsKind;
	}
}

const notices = {
	kind: function (tsKind: ts.SyntaxKind, get: TscWrapper) {
		log.error(
			log.identifier(__filename),
			'Did not discover a kind:',
			ts.SyntaxKind[tsKind],
			get.report,
		);
	},
	parser: {
		deepreport: function (
			this: TsDeclaration,
			isLocalTarget: boolean,
			get: TscWrapper,
		) {
			const reportType = isLocalTarget ? 'localTargetNode' : 'node';
			const reportMessage = `Did not parse a ${reportType}`;
			DoxProject.deepReport.call(
				this,
				__filename,
				'error',
				reportMessage,
				get,
				isLocalTarget,
			);
		},
	},
	parse: {
		debug: function (this: TsDeclaration, fncName: string) {
			log.debug(
				log.identifier(this),
				`[${fncName}]`,
				`[${log.toLine(this.get.nodeText)}]`,
				log.toLine(this.get.nodeDeclarationText),
			);
		},
	},
};

import {
	DeclarationGroup,
	TsDeclaration,
	TsReference,
	declarationMap,
	logger as log,
} from '../typedox';

/**
 * The highest level container of the project structure, after which the tree is recursive:
 *
 * &emsp;DoxProject\
 * &emsp;&emsp;|\
 * &emsp;&emsp;--- NpmPackage[]\
 * &emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;--- TsReference[]\
 * &emsp;&emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;&emsp;--- TsSourceFile[]\
 * &emsp;&emsp;&emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;&emsp;&emsp;--- TsDeclaration[]\
 * &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;--- **Branch**[]\
 * &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;|\
 * &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;...TsDeclaration...
 *
 */
export class Branch {
	parent: TsReference | Branch;
	_declarationBundle: Map<string, TsDeclaration> = new Map();
	_exportStarBundle: Map<string, TsDeclaration> = new Map();
	nameSpaces: Map<string, Branch> = new Map();
	classes: Map<string, TsDeclaration> = new Map();
	variables: Map<string, TsDeclaration> = new Map();
	functions: Map<string, TsDeclaration> = new Map();
	enums: Map<string, TsDeclaration> = new Map();
	types: Map<string, TsDeclaration> = new Map();
	default?: TsDeclaration;

	constructor(parent: TsReference | Branch, declarations: TsDeclaration[]) {
		this.parent = parent;
		declarations.forEach(this.bundleDeclaration);
		this.reExports.forEach(this.mergeReExportIntoDeclarations);
		this.declarationBundle.forEach(this.registerDeclaration);
	}
	private get reExports() {
		return [...this._exportStarBundle.values()];
	}
	private get declarationBundle() {
		return [...this._declarationBundle.values()];
	}
	private set Default(assignment: TsDeclaration) {
		if (this.default)
			log.throwError(
				log.identifier(this),
				'Can have only one default on a branch',
				log.stackTracer,
			);
		this.default = assignment;
	}
	private bundleDeclaration = (declaration: TsDeclaration) => {
		const { kind, name } = declaration;
		kind === DeclarationGroup.ExportStar
			? this.bundleExportStar(declaration)
			: this._declarationBundle.set(declaration.name, declaration);
	};
	private mergeReExportIntoDeclarations = (declaration: TsDeclaration) => {
		if (this._declarationBundle.has(declaration.name)) return;
		this._declarationBundle.set(declaration.name, declaration);
	};
	private registerDeclaration = (declaration: TsDeclaration) => {
		const { kind, name } = declaration;

		kind === DeclarationGroup.Module
			? this.registerNameSpace(declaration)
			: kind === DeclarationGroup.Class
			? this.classes.set(name, declaration)
			: kind === DeclarationGroup.Function
			? this.functions.set(name, declaration)
			: kind === DeclarationGroup.Variable
			? this.variables.set(name, declaration)
			: kind === DeclarationGroup.Enum
			? this.enums.set(name, declaration)
			: kind === DeclarationGroup.Type
			? this.types.set(name, declaration)
			: kind === DeclarationGroup.Default
			? (this.Default = declaration)
			: log.error(
					log.identifier(this),
					'Did not find a kind for a declaration: ',
					`${DeclarationGroup[kind]}\n`,
					declaration.get.report,
			  );
	};
	private bundleExportStar = (declaration: TsDeclaration) => {
		[...(declaration.children.values() || [])].forEach((declaration) => {
			if (this._exportStarBundle.has(declaration.name)) return;
			this._exportStarBundle.set(declaration.name, declaration);
		});
	};
	private registerNameSpace = (declaration: TsDeclaration) => {
		if (this.nameSpaces.has(declaration.name)) return;
		const children = Branch.getChildDeclarations(declaration.children);
		const newBranch = new Branch(this, children);
		this.nameSpaces.set(declaration.name, newBranch);
	};

	private static getChildDeclarations(children: declarationMap) {
		const values = children.values();
		return !!values ? [...values] : [];
	}
}

import * as ts from 'typescript';
import {
	DoxConfig,
	DoxProject,
	TsDeclaration,
	TsSourceFile,
	TscWrapper,
	fileMap,
	logger as log,
	tsc,
} from '../typedox';

/**
 * Builds a many to many mapping of all discovered `ts.Declarations`
 *
 * The mapping is applied into {@link dox.Declaration}.
 */
export class Relation extends DoxConfig {
	public get: TscWrapper;
	private name: string;
	private filesMap: fileMap;
	private declaration: TsDeclaration;
	private parent;

	constructor(parent: TsSourceFile, declaration: TsDeclaration) {
		super(parent.checker);
		this.parent = parent;
		this.declaration = declaration;
		this.get = declaration.get;
		this.name = declaration.name;
		this.filesMap = declaration.parent.parent.filesMap as any;

		this.mapRelationships(this.get.tsNode);
	}
	/** A `Map` of the local file's declarations keyed by name */
	private get localDeclarationMap() {
		const fileName = this.get.fileName;
		return this.filesMap.get(fileName)!.declarationsMap;
	}
	/** The local declaration in scope */
	private get localDoxDeclaration() {
		return this.localDeclarationMap.get(this.name)!;
	}

	/**
	 * Cycles through all candidate `ts.Node` kinds and maps them accordingly.
	 *
	 * This function can recurse to register alias's and exports / re-exports from imports etc.
	 * @param node The subject node, which may switch to the alias or import, etc.
	 * @param get The subject ts api wrapper, which may switch to the alias or re-export, etc.
	 * @param _isLocalTarget Flag to better inform the error logger about recursion
	 * @returns
	 */
	private mapRelationships(
		node: ts.Node,
		get = this.get,
		_isLocalTarget = false,
	) {
		if (!this.isSpecifierKind(node.kind) && !get.isExportStarChild) return;
		const errorMessage = `Did not map a ${
			_isLocalTarget ? 'localTargetNode' : 'node'
		} relationship`;

		get.isExportStarChild
			? this.mapExportStarChild(get)
			: ts.isNamespaceExport(node)
			? this.mapNameSpaceExport(get)
			: ts.isNamespaceImport(node)
			? this.mapNameSpaceImport(get)
			: ts.isModuleDeclaration(node)
			? this.mapModuleDeclaration(node)
			: ts.isExportSpecifier(node)
			? this.mapExportSpecifier(get)
			: ts.isImportClause(node)
			? this.mapImportClause(get)
			: ts.isExportAssignment(node)
			? this.mapExportAssignment(node)
			: ts.isImportSpecifier(node)
			? this.mapImportSpecifier(node, get)
			: DoxProject.deepReport.call(
					this,
					__filename,
					'error',
					errorMessage,
					get,
					_isLocalTarget,
			  );
	}
	private mapImportSpecifier(
		importSpecifier: ts.ImportSpecifier,
		get: TscWrapper,
	) {
		notices.map.debug.call(this, 'mapImportSpecifier');

		const name = importSpecifier.name.getText();
		const source = this.localDoxDeclaration;
		const targetSource = this.filesMap.get(get.targetFileName!);
		const target = targetSource?.declarationsMap.get(name)!;

		source.children.set(name, target);
		target.parents.push(source);
	}
	private mapExportSpecifier(get: TscWrapper) {
		notices.map.debug.call(this, 'mapExportSpecifier');

		if (get.localTargetDeclaration) {
			const local = get.localTargetDeclaration;
			const localGet = this.tsWrap(local);
			return this.mapRelationships(local, localGet, true);
		}
		log.error(
			log.identifier(this),
			'An unknown ts.ExportSpecifier was encountered:',
			get.report,
		);
	}
	private registerReExporter(symbol: ts.Symbol) {
		notices.map.debug.call(this, 'registerReExporter');

		tsc.parseExportStars.call(this, symbol).forEach((expression) => {
			const get = this.tsWrap(expression);
			this.mapExportStarChild(get);
		});
	}
	private mapExportStarChild(get: TscWrapper) {
		notices.map.debug.call(this, 'mapExportStarChild');

		const targetSource = this.filesMap.get(get.targetFileName!)!;
		const targetSymbols = targetSource.fileSymbol.exports;
		const source = this.localDoxDeclaration;

		targetSymbols?.forEach((symbol) => {
			if (tsc.isStarExport(symbol)) {
				return this.registerReExporter(symbol);
			}

			const name = symbol.getName();
			const target = targetSource.declarationsMap.get(name)!;

			if (source.children.has(name)) return;
			source.children.set(name, target);
			target.parents.push(source);
		});
	}
	private mapModuleDeclaration(moduleDeclaration: ts.ModuleDeclaration) {
		notices.map.debug.call(this, 'mapModuleDeclaration');

		const moduleSymbol = this.tsWrap(moduleDeclaration).tsSymbol;
		const source = this.localDoxDeclaration;

		moduleSymbol.exports?.forEach((symbol) => {
			const target = new TsDeclaration(this.parent, symbol);

			target.parents.push(source);
			source.children.set(target.name, target);
		});
	}
	private mapNameSpaceExport(get: TscWrapper) {
		notices.map.debug.call(this, 'mapNameSpaceExport');

		const remoteFile = this.filesMap.get(get.targetFileName!)!;
		const source = this.localDoxDeclaration;

		[...(remoteFile.fileSymbol.exports?.values() || [])]
			.map(exportStars.bind(this))
			.flat()
			.forEach(setRelations);

		function setRelations(symbol: ts.Symbol) {
			const name = symbol.getName();

			const target = remoteFile.declarationsMap?.get(name)!;
			source.children.set(name, target);
			target.parents.push(source);
		}

		function exportStars(this: Relation, symbol: ts.Symbol) {
			return tsc.isStarExport(symbol)
				? tsc.parseExportStars
						.call(this, symbol)
						.map((expression) => this.tsWrap(expression).tsSymbol)
				: symbol;
		}
	}
	private mapNameSpaceImport(get: TscWrapper) {
		notices.map.debug.call(this, 'mapNameSpaceImport');

		this.mapNameSpaceExport(get);
	}
	private mapImportClause(get: TscWrapper) {
		notices.map.debug.call(this, 'mapImportClause');

		const remoteFile = this.filesMap.get(get.targetFileName!)!;
		const source = this.localDoxDeclaration;
		const target = remoteFile.declarationsMap.get(this.name)!;

		target.parents.push(source);
		source.children.set(this.name, target);
	}
	private mapExportAssignment(exportAssignment: ts.ExportAssignment) {
		notices.map.debug.call(this, 'mapExportAssignment');

		const get = this.tsWrap(this.get.immediatelyAliasedSymbol!);
		this.mapRelationships(get.tsNode, get, true);

		//log.info(get.nodeDeclarationText);
		//log.inspect(get.tsNode, ['parent', 'flowNode', 'endFlowNode']);
		//this.mapRelationships(expressionGet.tsNode, expressionGet, true);
	}
}

const notices = {
	map: {
		debug: function (this: Relation, fncName: string) {
			log.debug(
				log.identifier(this),
				`[${fncName}]`,
				`[${log.toLine(this.get.nodeText)}]`,
				log.toLine(this.get.nodeDeclarationText),
			);
		},
	},
};

export * from './tsWrapperUtils';
export { wrap, cache, TsWrapperCache } from './TsWrapperCache';
export { TscWrapper } from './TsWrapper';

import * as ts from 'typescript';
import { Relation, TsSourceFile, TscWrapper, logger as log } from '../typedox';

export type symbolFlagString = keyof typeof ts.SymbolFlags;
export type typeFlagString = keyof typeof ts.SymbolFlags;
export type nodeKindString = keyof typeof ts.SyntaxKind;
export type tscWrapperReport = Partial<Record<keyof TscWrapper, any>>;
export const reportKeys: (keyof TscWrapper)[] = [
	'fileName',
	'targetFileName',
	'nodeText',
	'nodeDeclarationText',
	'localTargetDeclaration',
	'name',
	'alias',
	'kindString',
	'nodeFlagString',
	'symbolFlagString',
	'typeFlagString',
	'moduleSpecifier',
	'hasDeclarations',
	'hasValueDeclaration',
];
export function parseReportKey(this: TscWrapper, key: keyof TscWrapper) {
	let value = this[key];

	key === 'moduleSpecifier' &&
		value &&
		(value = (value as ts.Node).getText());
	key === 'localTargetDeclaration' &&
		value &&
		(value = (value as ts.Node).getText());

	return value;
}

export function getLocalTargetDeclaration(
	this: TscWrapper,
	declaration: ts.Identifier | ts.ExportSpecifier,
	checker: ts.TypeChecker,
) {
	const declarations = checker
		.getExportSpecifierLocalTargetSymbol(declaration)
		?.getDeclarations();
	if (declarations && declarations.length > 1)
		log.throwError(
			log.identifier(__filename),
			'Expected only one declaration in a local target symbol',
		);
	return !!declarations ? declarations[0] : undefined;
}

export function getTsNodeFromSymbol(this: TscWrapper, symbol: ts.Symbol) {
	const declarations = symbol.getDeclarations();
	return declarations && declarations.length === 1
		? (declarations[0] as ts.Node)
		: !!symbol.valueDeclaration
		? symbol.valueDeclaration
		: log.throwError(
				log.identifier(__filename),
				'Unexpected error while getting a ts.Node from a ts.Symbol',
		  );
}

export function getTsSymbolFromType(this: TscWrapper, type: ts.Type) {
	const symbol = type.getSymbol();
	return symbol
		? symbol
		: log.throwError(
				log.identifier(__filename),
				'Unexpected error while getting a ts.Symbol from a ts.Type',
		  );
}

export function getTsSymbolFromNode(
	this: TscWrapper,
	node: ts.Node,
	checker: ts.TypeChecker,
	fromName = false,
): ts.Symbol {
	if ('symbol' in node && !!node.symbol) return node.symbol as ts.Symbol;
	let symbol;
	try {
		symbol = checker.getSymbolAtLocation(node);
		if (!symbol)
			throw new Error(
				fromName
					? 'Could not get a ts.Symbol from the ts.Node'
					: 'Trying to get a ts.Symbol from tsNode.name',
			);
	} catch (error) {
		log.debug(log.identifier(this), (error as Error).message);
		if (!fromName && 'name' in node)
			return getTsSymbolFromNode.call(
				this,
				(node as any)['name'],
				checker,
				true,
			);
	}
	return !!symbol
		? (symbol as ts.Symbol)
		: log.throwError('Could not create a ts.Symbol from a ts.Node');
}

export function isStarExport(symbol: ts.Symbol) {
	return symbol.flags === ts.SymbolFlags.ExportStar;
}

export function parseExportStars(
	this: TsSourceFile | Relation,
	symbol: ts.Symbol,
) {
	return symbol
		.declarations!.map((declaration) => {
			return ts.isExportDeclaration(declaration)
				? declaration.moduleSpecifier
				: logError.call(this, declaration);
		})
		.filter((symbol) => !!symbol) as ts.Expression[];

	function logError(this: Object, declaration: ts.Declaration) {
		log.error(
			log.identifier(this),
			`Expected a ts.ExportDeclaration but got ts.${
				ts.SyntaxKind[declaration.kind]
			}`,
		);
	}
}

export function getModuleSpecifier(
	node: ts.Node,
	//seen = new Map<object, true>(),
): ts.Expression | undefined {
	//if (seen.has(node)) return undefined;
	//seen.set(node, true);
	if ('moduleSpecifier' in node) return node.moduleSpecifier as ts.Expression;
	if (!!node.parent) return getModuleSpecifier(node.parent); //,seen);
	return undefined;
}

import * as ts from 'typescript';
import { TscWrapper, logger as log, tsItem, tsc } from '../typedox';

export class TsWrapperCache {
	private _cache = {} as cache;
	protected checker: ts.TypeChecker;

	constructor(checker: ts.TypeChecker) {
		this.checker = checker;
	}

	protected cacheSet = (
		key: keyof cache,
		value: ts.Node | ts.Symbol | ts.Type,
	) => {
		this._cache[key] === undefined
			? (this._cache[key] = value as any)
			: notices.cacheSet.call(this, key);
	};

	protected cacheGetter = (wrapper: TscWrapper, key: keyof cache) => {
		return (this._cache[key] ??= cacheCallbacks[key].bind({
			wrapper,
			checker: this.checker,
		})() as any);
	};
}

export type cache = {
	[K in keyof typeof cacheCallbacks]: ReturnType<(typeof cacheCallbacks)[K]>;
};
const wrappers = new Map<ts.Node | ts.Symbol | ts.Type, TscWrapper>();
type wrapper = { wrapper: TscWrapper; checker: ts.TypeChecker };
const cacheCallbacks = {
	tsNode: function (this: wrapper): ts.Node {
		const { wrapper } = this;
		const node = tsc.getTsNodeFromSymbol.call(wrapper, wrapper.tsSymbol);
		wrappers.set(node, wrapper);

		return node;
	},
	tsSymbol: function (this: wrapper): ts.Symbol {
		const { wrapper, checker } = this;
		const symbol = wrapper.isType
			? tsc.getTsSymbolFromType.call(wrapper, wrapper.tsType)
			: wrapper.isNode
			? tsc.getTsSymbolFromNode.call(wrapper, wrapper.tsNode, checker)
			: notices.cacheCallbacks.tsSymbol.throw();
		wrappers.set(symbol, wrapper);

		return symbol;
	},
	tsType: function (this: wrapper): ts.Type {
		const { wrapper, checker } = this;
		const type = checker.getTypeOfSymbol(wrapper.tsSymbol);
		wrappers.set(type, wrapper);

		return type;
	},
	alias: function (this: wrapper) {
		const { wrapper } = this;
		return ts.isImportOrExportSpecifier(wrapper.tsNode)
			? wrapper.tsNode.propertyName?.getText()
			: undefined;
	},
	moduleSpecifier: function (this: wrapper) {
		const { wrapper } = this;
		return tsc.getModuleSpecifier(wrapper.tsNode);
	},
	targetFileName: function (this: wrapper) {
		const { wrapper, checker } = this;
		const target = wrapper.localTargetDeclaration;
		const get = target ? wrap(checker, target) : wrapper;

		if (!get.moduleSpecifier) return undefined;
		return checker
			.getSymbolAtLocation(get.moduleSpecifier)
			?.valueDeclaration?.getSourceFile().fileName;
	},
	fileName: function (this: wrapper) {
		const { wrapper } = this;
		return wrapper.tsNode.getSourceFile().fileName as string;
	},
	localTargetDeclaration: function (this: wrapper) {
		const { wrapper, checker } = this;
		if (!wrapper.isIdentifier && !wrapper.isExportSpecifier)
			return undefined;
		return tsc.getLocalTargetDeclaration.call(
			wrapper,
			wrapper.tsNode as ts.Identifier | ts.ExportSpecifier,
			checker,
		);
	},
	callSignatures: function (this: wrapper) {
		const { wrapper } = this;

		return wrapper.tsType.getCallSignatures();
	},
	nodeDeclarationText: function (this: wrapper) {
		const { wrapper, checker } = this;

		return rootNode(wrapper.tsNode).getText();

		function rootNode(node: ts.Node) {
			if (ts.isSourceFile(node.parent)) return node;
			return rootNode(node.parent);
		}
	},
	aliasedSymbol: function (this: wrapper) {
		const { wrapper, checker } = this;

		return checker.getAliasedSymbol(wrapper.tsSymbol);
	},
	immediateAliasedSymbol: function (this: wrapper) {
		const { wrapper, checker } = this;

		return checker.getImmediateAliasedSymbol(wrapper.tsSymbol);
	},
};

export function wrap(checker: ts.TypeChecker, item: tsItem): TscWrapper {
	const wrapped = wrappers.get(item) || new TscWrapper(checker, item);
	!wrappers.has(item) && wrappers.set(item, wrapped);

	return wrapped;
}

const notices = {
	cacheSet: function (this: TsWrapperCache, key: string) {
		log.error(
			log.identifier(this),
			'Tried to set existing cache key:',
			key,
		);
	},
	cacheCallbacks: {
		tsSymbol: {
			throw: function () {
				return log.throwError(
					log.identifier(__filename),
					'Could not find ts.Symbol',
				);
			},
		},
	},
};

import * as ts from 'typescript';
import { TsWrapperCache } from './TsWrapperCache';
import { logger as log, tsc } from '../typedox';

/**
 * Provides a convenience dox API wrapper around the typescript compiler API.
 */
export class TscWrapper extends TsWrapperCache {
	private objectClass: string;

	constructor(
		checker: ts.TypeChecker,
		tsItem: ts.Node | ts.Symbol | ts.Type,
	) {
		super(checker);
		this.objectClass = tsItem.constructor.name;

		this.isNode && this.cacheSet('tsNode', tsItem as ts.Node);
		this.isSymbol && this.cacheSet('tsSymbol', tsItem as ts.Symbol);
		this.isType && this.cacheSet('tsType', tsItem as ts.Type);
	}
	public get cacheGet() {
		return this.cacheGetter.bind(null, this);
	}
	public get isNode() {
		return this.objectClass === 'NodeObject';
	}
	public get isSymbol() {
		return this.objectClass === 'SymbolObject';
	}
	public get isType() {
		return this.objectClass === 'TypeObject';
	}
	public get tsNode(): tsc.cache['tsNode'] {
		return this.cacheGet('tsNode');
	}
	public get tsSymbol(): tsc.cache['tsSymbol'] {
		return this.cacheGet('tsSymbol') as tsc.cache['tsSymbol'];
	}
	public get tsType(): tsc.cache['tsType'] {
		return this.cacheGet('tsType');
	}
	/** The name of the `ts.Symbol` in scope */
	public get name() {
		return this.tsSymbol.name;
	}
	/** The name alias, if any, of a `ts.Node` */
	public get alias(): tsc.cache['alias'] {
		return this.cacheGet('alias');
	}

	public get kind() {
		return this.tsNode.kind;
	}
	/** String name of the `ts.Node` `ts.SyntaxKind` in scope */
	public get kindString() {
		return ts.SyntaxKind[this.tsNode.kind] as keyof typeof ts.SyntaxKind;
	}
	public get nodeFlag() {
		return this.tsNode.flags;
	}
	public get nodeFlagString() {
		return ts.NodeFlags[this.tsNode.flags];
	}
	public get symbolFlag() {
		return this.tsSymbol.flags;
	}
	public get symbolFlagString() {
		return ts.SymbolFlags[this.symbolFlag] as tsc.symbolFlagString;
	}
	public get typeFlag() {
		return this.tsType.getFlags();
	}
	public get typeFlagString() {
		return ts.TypeFlags[this.typeFlag] as tsc.typeFlagString;
	}
	public get moduleSpecifier(): tsc.cache['moduleSpecifier'] {
		return this.cacheGet('moduleSpecifier');
	}
	public get targetFileName(): tsc.cache['targetFileName'] {
		return this.cacheGet('targetFileName');
	}
	/** The file path of the declaration in scope */
	public get fileName(): tsc.cache['fileName'] {
		return this.cacheGet('fileName');
	}
	public get localTargetDeclaration(): tsc.cache['localTargetDeclaration'] {
		return this.cacheGet('localTargetDeclaration');
	}
	public get aliasedSymbol(): tsc.cache['aliasedSymbol'] {
		return this.cacheGet('aliasedSymbol');
	}
	public get immediatelyAliasedSymbol(): tsc.cache['immediateAliasedSymbol'] {
		return this.cacheGet('immediateAliasedSymbol');
	}
	public get callSignatures(): tsc.cache['callSignatures'] {
		return this.cacheGet('callSignatures');
	}
	public get hasDeclarations() {
		const { declarations } = this.tsSymbol;
		return declarations ? declarations.length : false;
	}
	public get hasValueDeclaration() {
		const { valueDeclaration } = this.tsSymbol;
		return valueDeclaration
			? (ts.SyntaxKind[valueDeclaration.kind] as tsc.nodeKindString)
			: false;
	}

	/** The string representation of the part of a declaration that is scope */
	public get nodeText() {
		return this.tsNode.getText();
	}
	/** The full string representation of a declaration ancestor where it is a direct child of the sourceFile. */
	public get nodeDeclarationText(): tsc.cache['nodeDeclarationText'] {
		return this.cacheGet('nodeDeclarationText');
	}

	public get isExportStarChild() {
		return (
			this.moduleSpecifier &&
			this.symbolFlag === ts.SymbolFlags.ValueModule &&
			this.kind === ts.SyntaxKind.StringLiteral
		);
	}

	public get isExportSpecifier() {
		return ts.isExportSpecifier(this.tsNode);
	}
	public get isIdentifier() {
		return ts.isIdentifier(this.tsNode);
	}

	/** A pre-formatted simple report on the scope. */
	public get report() {
		const report: tsc.tscWrapperReport = {};

		tsc.reportKeys.forEach((key) => {
			const value = tsc.parseReportKey.call(this, key);
			value !== undefined && (report[key] = value);
		});

		return report;
	}
}

const notices = {};

import {
	Branch,
	DoxProject,
	NpmPackage,
	TsDeclaration,
	TsReference,
	logger as log,
} from '../typedox';

export function serialiseProject(project: DoxProject) {
	const packageMap = new Map<string, NpmPackage>();
	project.npmPackages.forEach((pack) => packageMap.set(pack.name, pack));
	const packages = mapToObject<npmPackage>(packageMap, serialiseNpmPackage);

	return {
		packages,
	};
}
export function serialiseNpmPackage(npmPackage: NpmPackage) {
	const { version, name, tsReferences } = npmPackage;
	const referenceMap = new Map<string, TsReference>();
	tsReferences.forEach((ref) => referenceMap.set(ref.name, ref));
	const references = mapToObject<tsReference>(
		referenceMap,
		serialiseTsReference,
	);

	return {
		name,
		version,
		references,
	};
}

export function serialiseTsReference(reference: TsReference) {
	const branches = mapToObject<branch>(reference.treeBranches, branch);
	const branchName = reference.name;

	return { ...branches[branchName] };
}

function branch(treeBranch: Branch) {
	const {
		nameSpaces,
		functions,
		variables,
		classes,
		enums,
		default: def,
	} = treeBranch;

	return {
		default: def?.name,
		namespaces: mapToObject<nameSpaces>(nameSpaces, nameSpacesGroup),
		classes: mapToObject<classes>(classes, classesGroup),
		functions: mapToObject<functions>(functions, functionsGroup),
		enums: mapToObject<enums>(enums, enumsGroup),
		variables: mapToObject<variables>(variables, variablesGroup),
	};
}

function nameSpacesGroup(nameSpace: Branch) {
	return { ...branch(nameSpace) };
}

function classesGroup() {
	return {};
}

function variablesGroup() {
	return {};
}

function functionsGroup() {
	return {};
}

function enumsGroup() {
	return {};
}

function mapToObject<T extends targetFunction>(
	sourceMap: sourceMapTypes,
	targetFunction: targetFunctionTypes,
) {
	type R = ReturnType<T>;
	const newObject = Object.fromEntries(sourceMap);
	Object.keys(newObject).forEach((key) => {
		const newKeyValue = targetFunction(newObject[key]);
		newObject[key] = newKeyValue;
	});
	return newObject as {
		[k: string]: R;
	};
}

type sourceMapTypes =
	| Map<string, NpmPackage>
	| Map<string, TsReference>
	| Map<string, Branch>
	| Map<string, TsDeclaration>;

type targetFunctionTypes = npmPackage | tsReference | branch;
type targetFunction = (...args: any) => any;
type npmPackage = typeof serialiseNpmPackage;
type tsReference = typeof serialiseTsReference;
type branch = typeof branch;
type nameSpaces = typeof nameSpacesGroup;
type classes = typeof classesGroup;
type variables = typeof variablesGroup;
type functions = typeof functionsGroup;
type enums = typeof enumsGroup;

#!/usr/bin/env node

import {
	config,
	TsReference,
	DoxProject,
	NpmPackage,
	Branch,
	logger as log,
	projectOptions,
} from './typedox';

main();
export default function main(customOptions?: projectOptions) {
	if (log.isClRequestForHelp()) return;

	const doxProject = makeDoxProject(customOptions);
	const npmPackages = getNpmPackages(doxProject);
	const tsReferences = getTsReferences(npmPackages);

	discoverFilesAndDeclarations(tsReferences);
	buildRelationShips(tsReferences);
	growDocumentBranches(tsReferences);

	//log.inspect(doxProject.toObject);
}

function makeDoxProject(customOptions?: projectOptions) {
	const projectOptions = config.getDoxOptions(customOptions);
	const doxProject = new DoxProject(projectOptions);
	log.setLogLevel(doxProject.options.logLevel);

	return doxProject;
}
function getNpmPackages(doxProject: DoxProject) {
	return doxProject.npmPackages;
}

function getTsReferences(npmPackages: NpmPackage[]) {
	return npmPackages.map((npmPackage) => npmPackage.tsReferences).flat();
}

function discoverFilesAndDeclarations(tsReferences: TsReference[]) {
	tsReferences.forEach((tsReference) => {
		tsReference.discoverFiles();
		tsReference.discoverDeclarations();
	});
}

function buildRelationShips(tsReferences: TsReference[]) {
	tsReferences.forEach((tsReference) => tsReference.buildRelationships());
}

function growDocumentBranches(tsReferences: TsReference[]) {
	tsReferences.forEach((tsReference) => {
		const fileSources = getSourceFiles(tsReference);
		const rootDeclarations = TsReference.getDeclarationRoots(fileSources);
		const treeBranch = new Branch(tsReference, rootDeclarations);
		tsReference.treeBranches.set(tsReference.name, treeBranch);
	});

	function getSourceFiles(tsReference: TsReference) {
		return [...tsReference.filesMap.values()];
	}
}
